import { useEffect, useState } from 'react';
import { flushSync  } from 'react-dom';
import './index.css';
import SearchIcon from "./search.svg"

//46cefbd4
const API_URL = 'http://www.omdbapi.com?apikey=46cefbd4';

function App() {

  const [movies ,setMovies] = useState([]);

  const searchMovie = async (title) =>{
    const response = await fetch(`${API_URL}&s=${title}`);

    const data = await response.json();
    console.log(data);

    setMovies(data.Search);

  }


  useEffect(() => {

    searchMovie('batman');

  } , []);

  const movie1 = {
    Poster: "https://m.media-amazon.com/images/M/MV5BOTY4YjI2N2MtYmFlMC00ZjcyLTg3YjEtMDQyM2ZjYzQ5YWFkXkEyXkFqcGdeQXVyMTQxNzMzNDI@._V1_SX300.jpg",
    Title: "Batman Begins",
    Type: "movie",
    Year: "2005",
    imdbID: "tt0372784"
}

  return (
    <>
      <body className='bg-gray-900 flex flex-col h-screen items-center pt-32'>
          <h1 className='text-6xl bg-clip-text text-transparent bg-gradient-to-r from-indigo-500 to-purple-600 mb-10'>Movie Land
          </h1>

        <div className='flex justify-between border border-indigo-500 rounded-full pr-5 w-[80%] h-[60px] pl-6 shadow-lg shadow-gray-700' >
          <input className='text-gray-400 bg-transparent text-xl w-full h-full focus:outline-none' type="text"
          placeholder='write your movie title here'
          onChange={() => {}}
          />

          <img className='py-3 hover:cursor-pointer'
          src={SearchIcon}
          alt="Search"
          onClick={() => {}}
            />
        </div>

        <div className='grid grid-rows-1 '>
          {/* movie card */}
          <div className='flex flex-col relative rounded-3xl'>

            <div className=' absolute left-3 top-3'>
              <p className='text-white opacity-50'>
                {movie1.Year}
              </p>
            </div>

            <div>
              <img className=' rounded-xl' src={movie1.Poster} alt="" />
            </div>

            <div className=' bg-gray-600 absolute bottom-0 inset-x-0 pl-5 gap-5 py-5 rounded-b-xl'>
              <span className='text-lg text-white opacity-60'>
                {movie1.Type}
              </span>
              <h1 className='text-2xl text-indigo-500 font-bold'>
                {movie1.Title}
              </h1>
            </div>
          </div>
        </div>

      </body>

    </>
  );
}

export default App;
